mapper.select:
SELECT * FROM tb_movie where 1=1 AND poster_path IS NOT NULL AND backdrop_path IS NOT NULL AND original_language NOT IN ('ko','ja') AND overview IS NOT NULL
AND adult = 0
AND adult IN (0, 1) ORDER BY release_date DESC LIMIT 20;

SELECT * FROM tb_movie where 1=1 AND poster_path IS NOT NULL AND backdrop_path IS NOT NULL AND release_date <= now() AND original_language NOT IN ('ko','ja') AND overview IS NOT NULL
AND adult = 0
AND adult IN (0, 1) ORDER BY release_date DESC LIMIT 20;

SELECT * FROM tb_movie where 1=1 AND poster_path IS NOT NULL AND backdrop_path IS NOT NULL AND release_date <= now() AND original_language NOT IN ('ko','ja') AND overview IS NOT NULL
AND adult = 0
AND adult IN (0, 1) ORDER BY vote_average DESC LIMIT 20;

SELECT * FROM tb_movie where 1=1 AND poster_path IS NOT NULL AND backdrop_path IS NOT NULL AND release_date <= now() AND original_language NOT IN ('ko','ja') AND overview IS NOT NULL
AND adult = 0
AND adult IN (0, 1) ORDER BY popularity DESC LIMIT 20;

